#pragma config(Sensor, S1,     eyesIR,         sensorEV3_IRSensor)
#pragma config(Sensor, S2,     eyes,           sensorEV3_Ultrasonic)
#pragma config(Sensor, S3,     gyro,           sensorEV3_Gyro)
#pragma config(Motor,  motorA,          left,          tmotorEV3_Large, PIDControl, driveLeft, encoder)
#pragma config(Motor,  motorB,          head,          tmotorEV3_Medium, PIDControl, encoder)
#pragma config(Motor,  motorD,          right,         tmotorEV3_Large, PIDControl, driveRight, encoder)
//*!!Code automatically generated by 'ROBOTC' configuration wizard               !!*//
int Tp = 15;
//pusing -1 for left 1 for right

void depan(float masa){
	resetTimer(T1);
	while(getTimer(T1, seconds) < masa){
		setMotorSpeed(left, Tp);
		setMotorSpeed(right, Tp);
	}
	stopAllMotors();
	wait1Msec(250);
}

void pusing(int where){

	resetGyro(S3);
	wait1Msec(500);
	depan(2);
	while(abs(getGyroDegrees(S3)) < 90){
		setMotorSpeed(left,Tp/2 * where);
		setMotorSpeed(right,-Tp/2 * where);
	}

	setMotorSpeed(left, -5 * where);
	setMotorSpeed(right, 5 * where);
	wait1Msec(250);
	stopAllMotors();

}

void depanSampai(float gap){

	depan(3);

	while (getUSDistance(eyes)<=(2 * gap) && getIRDistance(eyesIR) > gap){
		setMotorSpeed(left, Tp);
		setMotorSpeed(right, Tp);
	}
	stopAllMotors();
	wait1Msec(250);
}

int tengok(){
	setMotorTarget(head, 90, 10);
	waitUntilMotorStop(head);
	return 0;
}

task main(){

	float gap = getUSDistance(eyes);

	int i = tengok();
}
